		DownfileService dfs = new DownfileService();
		Downfile d = new Downfile();
		Downfile orgf = dfs.GetDownfileById(id);

		d.setFileid(id);
//		if(downfileuploadForm.getThetitle().substring(0, 1).equals(" ") || downfileuploadForm.getThetitle().substring(0, 1).equals("")||downfileuploadForm.getThetitle().equals(null)){
//			d.setFilename(downfileuploadForm.getThefile().getFileName());
