		String filenameString = downfileuploadForm.getThefile().getFileName();
		
		if(!filenameString.equals(null) && !filenameString.equals("")){
			if(downfileuploadForm.getThetitle().substring(0, 1).equals(" ") || downfileuploadForm.getThetitle().equals("") || downfileuploadForm.getThetitle().equals(null)){
				d.setFilename(filenameString);
			}else{
				d.setFilename(downfileuploadForm.getThetitle());
			}
		}
		
		if (filenameString != null && !filenameString.equals("")) {
			FileNameFactory fnf = new FileNameFactory();
			filenameString = fnf.changeFileName(id, filenameString);
			if (filenameString == null) {
				return mapping.findForward(FERROR);
			}
			d.setFileurl(filenameString);
			GetSourceUrlService gsus = new GetSourceUrlService();
			FileOperate fo = new FileOperate();
			fo.DelOneFile(gsus.GetDownfileURL(), orgf.getFileurl(), false);
			if (!fo.AddOneFile(gsus.GetDownfileURL(), d.getFileurl(), downfileuploadForm.getThefile())) {
				return mapping.findForward(FERROR);
			}
		} else {
			d.setFileurl(orgf.getFileurl());
		}

