		String id = request.getParameter("id");
		if (id == null) {// ��ֹ����
			return mapping.findForward(ERROR);
		}

		GetSystemTime gst = new GetSystemTime();
		SchoolfileService ss = new SchoolfileService();
		Schoolfile schoolfile = new Schoolfile();
		Schoolfile orgs = ss.GetSchoolfileById(id);

		schoolfile.setFileid(id);
		schoolfile.setFiletitle(schoolfileuploadForm.getThetitle());
//		if(schoolfileuploadForm.getFiletitle().substring(0, 1).equals(" ") || schoolfileuploadForm.getFiletitle().substring(0, 1).equals("")||schoolfileuploadForm.getFiletitle().equals(null)){
//			schoolfile.setFilename(schoolfileuploadForm.getThetitle());
