		schoolfile.setFilenumber(schoolfileuploadForm.getThenumber());
		schoolfile.setFiledate(schoolfileuploadForm.getThedate());
		schoolfile.setFilecontent(schoolfileuploadForm.getMytext());
		String filenameString = schoolfileuploadForm.getThefile().getFileName();
		
		if(!filenameString.equals(null) && !filenameString.equals("")){
			if(schoolfileuploadForm.getFiletitle().substring(0, 1).equals(" ") || schoolfileuploadForm.getFiletitle().equals("") || schoolfileuploadForm.getFiletitle().equals(null)){
				schoolfile.setFilename(filenameString);
			}else{
				schoolfile.setFilename(schoolfileuploadForm.getFiletitle());
			}
		}
		
		if (filenameString != null && !filenameString.equals("")) {
			FileNameFactory fnf = new FileNameFactory();
			filenameString = fnf.changeFileName(id, filenameString);
			if (filenameString == null) {
				return mapping.findForward(FERROR);
			}
			schoolfile.setFileurl(filenameString);
			// ȡ���ļ������Ӧ�ĵ�ַ
			GetSourceUrlService gsus = new GetSourceUrlService();
			FileOperate fo = new FileOperate();
			fo.DelOneFile(gsus.GetSchoolfileURL(), orgs.getFileurl(), false);// ɾ��ԭ���ĸ���
			if (!fo.AddOneFile(gsus.GetSchoolfileURL(), schoolfile.getFileurl(), schoolfileuploadForm.getThefile())) {
				return mapping.findForward(FERROR);
			}
		} else {
			schoolfile.setFileurl(orgs.getFileurl());
		}

		if (ss.UpdateSchoolfile(schoolfile)) {
			return mapping.findForward(SUCCESS);
		} else {
			return mapping.findForward(DERROR);
		}
